                    binaryWriter1.Write(num7);
                    binaryWriter1.Write(num8);
                    binaryWriter1.Write(num9);
                    binaryWriter1.Write(num10);
                    binaryWriter1.Write(num11);
                    binaryWriter1.Write(num12);
